<?php 	header('Content-type: text/html; charset=UTF-8');	// define variables and set to empty values	$errors = array();	$nome = "";		$telefone = "";		$email = "";	$assunto = "";	$mensagemoriginal = "";		if ($_SERVER["REQUEST_METHOD"] == "POST") 	{	   //Valida o Nome	   if (empty($_POST["Nome"]))	   {			$errors[] = "Favor informar um nome para contato. <br />";	   } 	   else 	   {			$nome = test_input($_POST["Nome"]);			// check if name only contains letters and whitespace			if (!preg_match("/^[a-zA-Z ]*$/",$nome)) 			{				$errors[] = "Somente letras e espaços em branco são permitidos. <br />";			}				   } 	   //Valida o Telefone		$telefone = test_input($_POST["Telefone"]);	    	   	   		//Valida o Email	   if (empty($_POST["Email"])) 	   {			$errors[] = "Favor informar o email de contato. <br />";	   } 	   else 	   {			$Inscricao = test_input($_POST["Email"]);			// check if e-mail address is well-formed			if (!filter_var($Inscricao, FILTER_VALIDATE_EMAIL)) 			{			  $errors[] = "Email informado tem o formato inválido. <br />"; 			}	   }	   //Valida o Assunto	   if (empty($_POST["Assunto"]))	   {			$errors[] = "Favor informar o assunto do contato. <br />";	   } 	   else 	   {			$assunto = test_input($_POST["Assunto"]);	   } 		//Valida a Mensagem	   if (empty($_POST["Mensagem"])) 	   {			$errors[] = "Favor informar a mensagem do contato. <br />";	   } 	   else 	   {			$mensagemoriginal = test_input($_POST["Mensagem"]);	   } 	}	function test_input($data) 	{	   $data = trim($data);	   $data = stripslashes($data);	   $data = htmlspecialchars($data);	   return $data;	}		<script type="text/javascript">			print ('Mensagem enviada com sucesso')		</script>			if (empty($errors)) 	{		$destino = "contato@esportesolidario.com"; // Definimos Para quem vai ser enviado o email		$nome = $_POST['Nome']; // resgatar o nome digitado no formulário e grava na variavel $nome 		$email = $_POST['Email']; // resgatar o email digitado no formulário e grava na variavel $email 		$assunto = $_POST['Assunto']; // resgatar o assunto digitado no formulário e grava na variavel $assunto 		$origem = "contato@esportesolidario.com"; 	//  Definimos a origem do email		date_default_timezone_set('America/Sao_Paulo');		$data = date('Y-m-d'); //data 		$hora = date('H:i'); //hora		//Construção do assunto do email		$mensagem = "<strong>Nome: </strong>".$nome; 		$mensagem .= "<br> <strong>Telefone: </strong>".$telefone; 					$mensagem .= "<br> <strong>Email: </strong>".$email; 			$mensagem .= "<br> <strong>Mensagem: </strong>".$mensagemoriginal; 		$mensagem .= "<br> <strong>Mensagem recebida em: </strong>".$data."<strong> as </strong>".$hora; 			//Construção em HTML		$mensagemHTML = '<p>Mensagem recebida com sucesso</p><p>'.$mensagem.'</p>';			//6 – agora inserimos as codificações corretas e tudo mais. 		$headers .= "MIME-Version: 1.0\n\r"; 		$headers = "Content-Type:text/html; charset=UTF-8\n\r"; 		$headers .= "From: esportesolidario.com <faleconosco@esportesolidario.com>\n\r"; //Vai ser mostrado que o email partiu deste email e seguido do nome 		$headers .= "X-Sender: <contato@esportesolidario.com>\n\r"; //email do servidor que enviou 		$headers .= "X-Mailer: PHP v".phpversion()."\n\r"; 		$headers .= "X-IP: ".$_SERVER['REMOTE_ADDR']."\n\r"; 		$headers .= "Return-Path: esportesolidario.com <faleconosco@esportesolidario.com>\n\r"; //caso a mensagem seja respondida vai para este email.  		if(!mail($destino, $assunto, $mensagemHTML, $headers ,"-r".$origem))		{ // Se for Postfix			$headers .= "Return-Path: " . $origem . $quebra_linha; // Se "não for Postfix"			mail($destino, $assunto, $mensagemHTML, $headers );		}				<script type="text/javascript">			print ('Mensagem enviada com sucesso')		</script>		header("Location: http://www.universitariosolidario.com.br/SiteNovo/Conheca/FaleConosco.html"); /* Redirect browser */		exit();		//mail($para, $assunto, $mensagem, $headers); //função que faz o envio do email. 	}	else	{		echo "<p style=\"color:#F00\">";		foreach($errors as $errormessages)		{			echo "$errormessages <br /><br />";		}        echo "<div align='center'><a href=javascript:history.go(-1)>Voltar</a></div>";		exit; 			}?><html lang="en">  <head>	</head>	<body>	</body></html>